{
  "name": "copy-files-and-code",
  "displayName": "Copy Files and Code",
  "description": "VS Code extension to copy files and code",
  "version": "1.0.0",
  "engines": {
      "vscode": "^1.46.0"
  },
  "categories": [
      "Other"
  ],
  "activationEvents": [
      "onCommand:copyfilesandcode.copyFiles",
      "onCommand:copyfilesandcode.generateCopyList",
      "onCommand:copyfilesandcode.restoreFiles",
      "onCommand:copyfilesandcode.openWebview"
  ],
  "main": "./out/extension.js",
  "contributes": {
      "commands": [
          {
              "command": "copyfilesandcode.copyFiles",
              "title": "Copy Files & Code"
          },
          {
              "command": "copyfilesandcode.generateCopyList",
              "title": "Generate Copy List"
          },
          {
              "command": "copyfilesandcode.restoreFiles",
              "title": "Restore Files"
          },
          {
              "command": "copyfilesandcode.openWebview",
              "title": "Open Webview"
          }
      ]
  },
  "scripts": {
      "vscode:prepublish": "npm run compile",
      "compile": "tsc -p ./",
      "watch": "tsc -watch -p ./",
      "postinstall": "node ./node_modules/vscode/bin/install",
      "test": "node ./out/test/runTest.js"
  },
  "devDependencies": {
      "@types/node": "^14.14.41",
      "@types/vscode": "^1.56.0",
      "typescript": "^4.3.2",
      "vscode-test": "^1.5.2"
  }
}

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';
import { generateCopyList } from './generateCopyList';

export const copyFiles = async () => {
    const workspaceFolders = vscode.workspace.workspaceFolders;
    if (!workspaceFolders) {
        vscode.window.showErrorMessage('No workspace is open');
        return;
    }

    const rootPath = workspaceFolders[0].uri.fsPath;
    const copyListPath = path.join(rootPath, 'copylist.txt');
    const copiedFilesPath = path.join(rootPath, 'copiedfiles.json');

    if (!fs.existsSync(copyListPath)) {
        generateCopyList();
    }

    const copyList = fs.readFileSync(copyListPath, 'utf-8').split('\n').filter(Boolean);
    let copiedFiles: any[] = [];

    const readDirectory = (dirPath: string) => {
        const files = fs.readdirSync(dirPath);
        for (const file of files) {
            const filePath = path.join(dirPath, file);
            if (filePath === copiedFilesPath) {
                continue;  // Skip the copiedfiles.json file
            }
            if (fs.lstatSync(filePath).isDirectory()) {
                readDirectory(filePath);
            } else {
                const fileContent = fs.readFileSync(filePath, 'utf-8');
                const relativePath = path.relative(rootPath, filePath);
                copiedFiles.push({filePath: relativePath, content: fileContent});
            }
        }
    };

    for (const item of copyList) {
        const itemPath = path.join(rootPath, item);
        if (itemPath === copiedFilesPath) {
            continue;  // Skip the copiedfiles.json file
        }
        if (fs.existsSync(itemPath)) {
            if (fs.lstatSync(itemPath).isDirectory()) {
                readDirectory(itemPath);
            } else {
                const fileContent = fs.readFileSync(itemPath, 'utf-8');
                const relativePath = path.relative(rootPath, itemPath);
                copiedFiles.push({filePath: relativePath, content: fileContent});
            }
        }
    }

    fs.writeFileSync(copiedFilesPath, JSON.stringify(copiedFiles, null, 2));

    vscode.window.showInformationMessage('Files copied to copiedfiles.json');
};

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';
import { readDirectory } from './readDirectory';
import { generateCopyList } from './generateCopyList';
import { restoreFiles } from './restoreFiles';
import { copyFiles } from './copyFiles';

let copyListContent = '';
let copiedFilesContent = [];

export function activate(context: vscode.ExtensionContext) {

    // Load initial state
    const rootPath = vscode.workspace.workspaceFolders![0].uri.fsPath;
    const copyListPath = path.join(rootPath, 'copylist.txt');
    const copiedFilesPath = path.join(rootPath, 'copiedfiles.json');

    copyListContent = fs.readFileSync(copyListPath, 'utf-8');
    copiedFilesContent = JSON.parse(fs.readFileSync(copiedFilesPath, 'utf8'));

    // Register commands   
    let disposable = vscode.commands.registerCommand('copyfilesandcode.copyFiles', copyFiles);
    let generateDisposable = vscode.commands.registerCommand('copyfilesandcode.generateCopyList', generateCopyList);
    let restoreDisposable = vscode.commands.registerCommand('copyfilesandcode.restoreFiles', restoreFiles);
    context.subscriptions.push(disposable, generateDisposable, restoreDisposable);

    let openWebView = vscode.commands.registerCommand('copyfilesandcode.openWebview', function () {
        const panel = vscode.window.createWebviewPanel(
            'copyFilesAndCodeCommands',
            'Copy Files And Code Commands',
            vscode.ViewColumn.One,
            {enableScripts: true} 
        );

        panel.webview.html = getWebviewContent(context);

        // Handle messages from webview
        panel.webview.onDidReceiveMessage(
            message => {
                switch (message.command) {
                    case 'saveCopyList': 
                        copyListContent = message.content;
                        fs.writeFileSync(copyListPath, copyListContent);
                        return;
                    case 'saveCopiedFiles':
                        copiedFilesContent = JSON.parse(message.content);
                        fs.writeFileSync(copiedFilesPath, JSON.stringify(copiedFilesContent));                          
                        return;
                }
            }
        );
    });
    context.subscriptions.push(openWebView);
}

// Omitted other functions for brevity

function getWebviewContent(context: vscode.ExtensionContext) {
  return `
    <html>
      <body>
        <h1>Edit Copy List</h1>
        <textarea id="copyList">${copyListContent}</textarea>
        <button onclick="saveCopyList()">Save</button>

        <h1>Edit Copied Files</h1>  
        <textarea id="copiedFiles">${JSON.stringify(copiedFilesContent, null, 2)}</textarea>
        <button onclick="saveCopiedFiles()">Save</button>

        <script>
          const vscode = acquireVsCodeApi();

          function saveCopyList() {
            vscode.postMessage({
              command: 'saveCopyList',
              content: document.getElementById('copyList').value
            })
          }

          function saveCopiedFiles() {
            vscode.postMessage({
              command: 'saveCopiedFiles',
              content: document.getElementById('copiedFiles').value
            })
          }
        </script>
      </body>
    </html>`;
}

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';

export const generateCopyList = () => {
    const workspaceFolders = vscode.workspace.workspaceFolders;
    if (!workspaceFolders) {
        vscode.window.showErrorMessage('No workspace is open');
        return;
    }

    const rootPath = workspaceFolders[0].uri.fsPath;
    const copyListPath = path.join(rootPath, 'copylist.txt');
    const copiedFilesPath = path.join(rootPath, 'copiedfiles.json');

    if (fs.existsSync(copyListPath) && fs.readFileSync(copyListPath, 'utf-8').trim() !== '') {
        const copyList = fs.readFileSync(copyListPath, 'utf-8').split('\n').filter(Boolean);
        let newCopyList = '';

        for (const item of copyList) {
            const itemPath = path.join(rootPath, item);
            if (fs.existsSync(itemPath) && fs.lstatSync(itemPath).isDirectory()) {
                fs.readdirSync(itemPath).forEach(file => {
                    const filePath = path.join(item, file);
                    newCopyList += filePath + '\n';
                });
            } else {
                newCopyList += item + '\n';
            }
        }

        fs.writeFileSync(copyListPath, newCopyList);
    } else {
        let copyList = '';

        fs.readdirSync(rootPath).forEach(file => {
            const filePath = path.join(rootPath, file);
            if (filePath !== copyListPath && filePath !== copiedFilesPath) {  // Exclude the copylist.txt and copiedfiles.json files
                copyList += file + '\n';
            }
        });

        fs.writeFileSync(copyListPath, copyList);
    }

    vscode.window.showInformationMessage('copylist.txt generated');
};

import * as fs from 'fs';
import * as path from 'path';
import { promisify } from 'util';

const readdirAsync = promisify(fs.readdir);
const readFileAsync = promisify(fs.readFile);

export const readDirectory = async (
    dirPath: string,
    rootPath: string,
    visitedDirs: Set<string>,
    processFile: (relativePath: string, fileContent: string) => void
) => {
    if (visitedDirs.has(dirPath)) {
        return;
    }
    visitedDirs.add(dirPath);

    try {
        const files = await readdirAsync(dirPath);
        for (const file of files) {
            const filePath = path.join(dirPath, file);
            const stat = await fs.promises.lstat(filePath);

            // Handle symbolic links
            if (stat.isSymbolicLink()) {
                continue;
            }

            if (stat.isDirectory()) {
                await readDirectory(filePath, rootPath, visitedDirs, processFile);
            } else {
                const fileContent = await readFileAsync(filePath, 'utf-8');
                const relativePath = path.relative(rootPath, filePath);
                processFile(relativePath, fileContent);
            }
        }
    } catch (error) {
        console.error(error);
    }
};

import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';

export const restoreFiles = () => {
    const workspaceFolders = vscode.workspace.workspaceFolders;
    if (!workspaceFolders) {
        vscode.window.showErrorMessage('No workspace is open');
        return;
    }

    const rootPath = workspaceFolders[0].uri.fsPath;
    const copiedFilesPath = path.join(rootPath, 'copiedfiles.json');

    if (!fs.existsSync(copiedFilesPath)) {
        vscode.window.showErrorMessage('No copied files to restore');
        return;
    }

    const copiedFiles = JSON.parse(fs.readFileSync(copiedFilesPath, 'utf-8'));

    for (const copiedFile of copiedFiles) {
        const filePath = path.join(rootPath, copiedFile.filePath);
        const fileContent = copiedFile.content;

        // Check if the path is a directory
        if (fs.existsSync(filePath) && fs.lstatSync(filePath).isDirectory()) {
            continue;  // Skip directories
        }

        fs.writeFileSync(filePath, fileContent);
    }

    vscode.window.showInformationMessage('Files restored from copiedfiles.json');
};
